mapper.select:
SELECT COUNT(*)
  FROM MEMBER
 WHERE MEMBER_ID = #{memberId}

SELECT COUNT(*)
  FROM MEMBER
 WHERE MEMBER_EMAIL = #{email}

mapper.insert:
 		INSERT INTO MEMBER(MEMBER_NO
			 				 , MEMBER_ID
			                 , MEMBER_PWD
			                 , MEMBER_NAME
			                 , MEMBER_PHONE
			                 , MEMBER_EMAIL)
		VALUES(SEQ_MEM.NEXTVAL
			 , #{memberId}
		     , #{memberPwd}
		     , #{memberName}
		     , #{memberPhone}
		     , #{memberEmail})

mapper.update:
UPDATE MEMBER
   SET MEMBER_NAME = #{memberName}
     , MEMBER_PHONE = #{memberPhone}
     , MEMBER_EMAIL = #{memberEmail}
 WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER
   SET STATUS = 'N'
 WHERE MEMBER_NO =#{memberNo}

UPDATE MEMBER
   SET MEMBER_PWD = #{memberPwd}
 WHERE MEMBER_NO = #{memberNo}
